mapper.delete:
update notice set title =#{title}, content=#{content}, 
				filename=#{filename, jdbcType=VARCHAR}, 
				filepath = #{filepath, jdbcType=VARCHAR}
where id = #{id}

mapper.select:
select *
from ( select rownum no, n.*, (select name from member where id = n.writer) name from (select * from notice order by id) n order by no desc)
where no between #{beginList} and #{endList}
